import React from "react";

const Footer = () => {
  return (
    <section className="footer">
      <div className="footer-content">
        <div className="grid-container">
          <div className="content">
            <h2>
              Lets start a
              <span>
                <img
                  src="data:image/png;base64,"
                  alt=""
                />
              </span>
              project together
            </h2>
            <p>
              We work closely with our clients to understand their objectives,
              target audience, and unique needs. We use our creative skills to
              translate these requirements and practical design solutions.
            </p>
            <div className="info">
              <div className="phone-no">
                <h5>Give us a call:</h5>
                <p>
                  <a href="tel:(123) 456-7890">(123) 456-7890</a>
                </p>
              </div>
              <div className="mail">
                <h5>Send us an email:</h5>
                <p>
                  <a href="mailto:info@mthemeus.com">info@mthemeus.com</a>
                </p>
              </div>
            </div>
          </div>
          <div className="form"></div>
        </div>
        <div className="copyright-content">
          <div className="logo"></div>
          <div className="copyright"></div>
        </div>
      </div>
    </section>
  );
};

export default Footer;
